import Navbar from '../components/Navbar';

import Fetch from 'isomorphic-unfetch';
import Layout from '../components/Layout';

const Index = props => (
  <Layout>
    <div>
      <h2>Hello HomePage</h2>
      <p>Holla</p>
      <p>{props.info}</p>
    </div>
  </Layout>
);

Index.getInitialProps = async function () {
  const res = await fetch(
    'http://newsapi.org/v2/everything?q=bitcoin&from=2020-06-20&sortBy=publishedAt&apiKey=0ff232978e2d41c7872088eb9be53d23'
  );
  const data = await res.json();

  return {
    info: data.articles.map(article => (
      <div>
        <ul>
          <li key={article.description}>{article.description}</li>
        </ul>
      </div>
    )),
  };
};

export default Index;
