import Fetch from 'isomorphic-unfetch';
import Link from 'next/link';

const newsData = [
  {
    title: 'Latest Phones in America',
    author: 'Marlin Brookes',
    category: 'sport',
  },
  { title: 'Oldest Cars in Nigeria', author: 'Sholla Mary', category: 'sport' },
  { title: 'How to make soda', author: 'Ojie baba', category: 'entertainment' },
];

class News extends React.Component {
  state = {
    category: 'sport',
  };

  render() {
    return (
      <div>
        <br />
        {newsData.map((e, index) => (
          <div key={index}>
            <ul className="list-group">
              <li className="list-group-item">{`Title: ${e.title}`}</li>
              <li className="list-group-item">{`Author: ${e.author}`}</li>
            </ul>
          </div>
        ))}
      </div>
    );
  }
}

export default News;

