import Fetch from 'isomorphic-unfetch';
import Link from 'next/link';

const News = ({ newsList }) => (
  <div>
    {newsList.articles.map((e, index) => (
      <div key={index}>
        <ul className="list-group">
          <li className="list-group-item">{`Title: ${e.articles.title}`}</li>
          <li className="list-group-item">{`Author: ${e.articles.author}`}</li>
        </ul>
        <br />
      </div>
    ))}
  </div>
);

export default News;

News.getInitialProps = async function () {
  const res = await fetch(
    'http://newsapi.org/v2/top-headlines?sources=techcrunch&apiKey=0ff232978e2d41c7872088eb9be53d23'
  );
  const newsList = await res.json();

  return { newsList: newsList.articles };
};
