import Fetch from 'isomorphic-unfetch';
import Link from 'next/link';

const News = props => (
  <div>
    {props.newsList.articles.map((e, index) => (
      <div key={index}>
        <div className="row">
          <div className="col-md-8">
            <p className="text-justify colored">
              <strong>Title:</strong>{' '}
              <i className="bx bx-tada bxl-whatsapp"></i> {e.title}
              <br />
              <strong>Author:</strong>{' '}
              <i className="bx bx-tada bxs-phone-call"></i> {e.author}
              <br />
              <strong>Description:</strong> <i className=""></i> {e.description}
              <br />
            </p>
          </div>
          <div className="col-md-4">Holla</div>
        </div>
      </div>
    ))}
    <style jsx>
      {`
        .colored {
          background-image: linear-gradient(15deg, #18547a 90%, #80d0c7 100%);
          padding 20px;
          color: #fff;
        }
      `}
    </style>
  </div>
);
export default News;
