import Fetch from 'isomorphic-unfetch';
import Link from 'next/link';
import NewsUploadForm from '../components/newsUploadForm';
//import NewsCategory from '../components/NewsCategory';

const News = props => (
  <div className="allNews">
    <div className="row">
      <div className="col-md-8">
        <h2>
          <br />
          Breaking News
        </h2>
        {props.newsList.articles.map((e, index) => (
          <div key={index}>
            <div className="">
              <div className="">
                <p className="text-justify colored">
                  <strong>Title:</strong>{' '}
                  <span id="capitalized">{e.title}</span>
                  <br />
                  <strong>Date Published:</strong> {e.publishedAt}
                  <br />
                  <strong>Author:</strong>{' '}
                  <i className="bx bx-tada bxs-phone-call"></i> {e.author}
                  <br />
                  <strong>Description:</strong> {e.description}
                  <br />
                  <strong>Content:</strong> {e.content}
                  <br />
                  <strong>LinkToImage:</strong>
                  <a href="" target="_blank">
                    {''}
                    {e.urlToImage}
                  </a>
                  <hr id="bottomStyle" />
                  <br />
                </p>
              </div>
            </div>
          </div>
        ))}{' '}
      </div>
      <div className="col-md-4">
        <NewsUploadForm />
      </div>
    </div>
    <style jsx>
      {`
        .colored {
          padding: 10px;
          background-color: #fafafa;
          border-bottt
        }

        p {
          font-size: 17px;
          font-family: Arial;
        }

        .allNews {
          padding-right: 20px;
        }

        #capitalized {
          text-transform: capitalize;
          font-weight: bold;
        }
      `}
    </style>
  </div>
);
export default News;
