import Link from 'next/link';

const newsData = [
  { title: 'Latest Phones in America', author: 'Marlin Brookes' },
  { title: 'Oldest Cars in Nigeria', author: 'Sholla Mary' },
  { title: 'How to make soda', author: 'Ojie baba' },
];

const News = () => (
  <div>
    {newsData.map((e, index) => (
      <div key={index}>
        <ul>
          <li>{`Author: ${e.author}`}</li>
          <li>{`Title: ${e.title}`}</li>
        </ul>
      </div>
    ))}
  </div>
);

export default News;

