import Link from 'next/link';

const newsData = [
  {
    title: 'Latest Phones in America',
    author: 'Marlin Brookes',
    category: 'sport',
  },
  { title: 'Oldest Cars in Nigeria', author: 'Sholla Mary', category: 'sport' },
  { title: 'How to make soda', author: 'Ojie baba', category: 'entertainment' },
];

const News = () => (
  <div>
    {newsData.map((e, index) => (
      <div key={index}>
        <ul>
          <li>{`Author: ${e.author}`}</li>
          <li>{`Title: ${e.title}`}</li>
        </ul>
      </div>
    ))}
  </div>
);

export default News;

