import Fetch from 'isomorphic-unfetch';
import Link from 'next/link';
import NewsUploadForm from '../components/newsUploadForm';
//import NewsCategory from '../components/NewsCategory';

const News = props => (
  <div>
    <h3>
      <u>Breaking News</u>
    </h3>
    {props.newsList.articles.map((e, index) => (
      <div key={index}>
        <div className="row">
          <div className="col-md-8">
            <p className="text-justify colored">
              <strong>Title:</strong>{' '}
              <i className="bx bx-tada bxl-whatsapp"></i> {e.title}
              <br />
              <strong>Author:</strong>{' '}
              <i className="bx bx-tada bxs-phone-call"></i> {e.author}
              <br />
              <strong>Description:</strong> {e.description}
              <br />
              <strong>Date Published:</strong> {e.publishedAt}
              <br />
              <strong>Content:</strong> {e.content}
              <br />
              <strong>ImageLink:</strong>
              <a href="" target="_blank">
                {' '}
                {e.urlToImage}
              </a>
              <br />
            </p>
          </div>
          <div className="col-md-4">
            <NewsUploadForm />
          </div>
        </div>
      </div>
    ))}
    <style jsx>
      {`
        .colored {
          padding-bottom: 10px;
        }

        p {
          font-size: 17px;
        }
      `}
    </style>
  </div>
);
export default News;
