const Jumbotron = () => (
  <div className="card card-image">
    <div className="text-white text-center">
      <div className="py-2">
        <h5 className="h5">
          <i className="fas fa-camera-retro"></i> Photography
        </h5>
        <h2 className="card-title h2 my-2 py-2">
          Jumbotron with image overlay
        </h2>
        <a className="btn peach-gradient">
          <i className="fas fa-clone left"></i> View project
        </a>
      </div>
    </div>

    <style jsx>
      {`
        .card {
          background-image: linear-gradient(15deg, #18547a 0%, #80d0c7 100%);
          color: #fff;
        }

        h2 {
          color: #fff;
        }
      `}
    </style>
  </div>
);
export default Jumbotron;
