import Fetch from 'isomorphic-unfetch';
import Link from 'next/link';
//import NewsCategory from '../components/NewsCategory';

const News = props => (
  <div>
    <h3>
      <u>Breaking News</u>
    </h3>
    {props.newsList.articles.map((e, index) => (
      <div key={index}>
        <div className="row">
          <div className="col-md-10">
            <p className="text-justify colored">
              <strong>Title:</strong>{' '}
              <i className="bx bx-tada bxl-whatsapp"></i> {e.title}
              <br />
              <strong>Author:</strong>{' '}
              <i className="bx bx-tada bxs-phone-call"></i> {e.author}
              <br />
              <strong>Description:</strong> <i className=""></i> {e.description}
              <br />
            </p>
          </div>
          <div className="col-md-2">
            <p>Date: {e.publishedAt}</p>
          </div>
        </div>
      </div>
    ))}
    <style jsx>
      {`
        .colored {
          padding-bottom: 10px;
        }

        p {
          font-size: 16px;
        }
      `}
    </style>
  </div>
);
export default News;
